(function(a){a.fn.clickableRows=function(){var b=a(this);b.find("tbody tr").each(function(){var d=a(this).find("td");if(d.length>0){cellLink=a(d[0]).find("a");if(cellLink.length==0){cellLink=a(d[d.length-1]).find("a")}if(cellLink.length>0){var c=a(cellLink[0]).attr("href");d.css("cursor","pointer").click(function(f){if(a(this).find("a").length>0){return}window.location.href=c})}}})}})(jQuery);